mapper.select:
SELECT 
FROM tb_gb_inspection_item t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.report_id = #{reportId}


    AND t.item_category = #{itemCategory}


    AND t.item_type = #{itemType}


    AND t.item_code = #{itemCode}


    AND t.total_data_count = #{totalDataCount}


    AND t.error_data_count = #{errorDataCount}


    AND t.total_vehicle_count = #{totalVehicleCount}


    AND t.error_vehicle_count = #{errorVehicleCount}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
