mapper.delete:
DELETE FROM tb_gb_inspection_report t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_gb_inspection_report t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.start_time = #{startTime}


    AND t.end_time = #{endTime}


    AND t.report_type = #{reportType}


    AND t.report_state = #{reportState}


    AND t.vehicle = #{vehicle}


    AND t.data_start_time = #{dataStartTime}


    AND t.data_end_time = #{dataEndTime}


    AND t.vehicle_count = #{vehicleCount}


    AND t.vehicle_error_count = #{vehicleErrorCount}


    AND t.scene = #{scene}


    AND t.scene_start_time = #{sceneStartTime}


    AND t.scene_end_time = #{sceneEndTime}


    AND t.message_count = #{messageCount}


    AND t.message_error_count = #{messageErrorCount}


    AND t.data_count = #{dataCount}


    AND t.data_error_count = #{dataErrorCount}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
