mapper.delete:
DELETE FROM tb_vehicle_gb_alarm t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_vehicle_gb_alarm t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.vin = #{vin}


    AND t.alarm_time = #{alarmTime}


    AND t.restoration_time = #{restorationTime}


    AND t.alarm_flag = #{alarmFlag}


    AND t.alarm_level = #{alarmLevel}


    AND t.message_data = #{messageData}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_vehicle_gb_alarm t
WHERE t.row_valid = 1


    AND t.vin = #{vin}


    AND t.alarm_flag = #{alarmFlag}


    AND t.alarm_level = #{alarmLevel}


    AND date_format(t.alarm_time,'%Y%m%d') >= date_format(#{beginTime},'%Y%m%d')


    AND date_format(t.alarm_time,'%Y%m%d') <= date_format(#{endTime},'%Y%m%d')

ORDER BY t.id DESC
