mapper.delete:
DELETE FROM tb_vehicle_gb_message t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_vehicle_gb_message t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.vin = #{vin}


    AND t.parse_time = #{parseTime}


    AND t.message_time = #{messageTime}


    AND t.command_flag = #{commandFlag}


    AND t.message_data = #{messageData}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT FROM tb_vehicle_gb_message t WHERE t.row_valid = 1
AND t.vin = #{vin}
AND t.command_flag = #{commandFlag}
AND date_format(t.message_time,'%Y%m%d') >= date_format(#{beginTime},'%Y%m%d')
AND date_format(t.message_time,'%Y%m%d') <= date_format(#{endTime},'%Y%m%d')
ORDER BY t.id DESC
